/* eslint-disable no-restricted-globals */
self.__WB_DISABLE_DEV_LOGS = true;

// ë§Œì•½ __WB_MANIFESTê°€ ì •ì˜ë˜ì§€ ì•Šì•˜ë‹¤ë©´ ë¹ˆ ë°°ì—´ë¡œ ì´ˆê¸°í™”
self.__WB_MANIFEST = self.__WB_MANIFEST || [];

// precache ëª©ë¡ì— index.html ìˆ˜ë™ ì¶”ê°€
self.__WB_MANIFEST.push({ url: '/index.html', revision: '1' });

// Workbox ë¼ì´ë¸ŒëŸ¬ë¦¬ ë¶ˆëŸ¬ì˜¤ê¸°
importScripts('https://storage.googleapis.com/workbox-cdn/releases/6.5.4/workbox-sw.js');

if (workbox) {
  console.log('âœ… Workbox loaded successfully');

  workbox.core.clientsClaim();

  // Precache all assets generated by the build process.
  workbox.precaching.precacheAndRoute(self.__WB_MANIFEST);

  // Set up App Shell-style routing
  const fileExtensionRegexp = new RegExp('/[^/?]+\\.[^/]+$');
  workbox.routing.registerRoute(
    ({ request, url }) => {
      if (request.mode !== 'navigate') return false;
      if (url.pathname.startsWith('/_')) return false;
      if (url.pathname.match(fileExtensionRegexp)) return false;
      return true;
    },
    workbox.precaching.createHandlerBoundToURL('/index.html')
  );

  // Runtime caching for .png requests
  workbox.routing.registerRoute(
    ({ url }) => url.origin === self.location.origin && url.pathname.endsWith('.png'),
    new workbox.strategies.StaleWhileRevalidate({
      cacheName: 'images',
      plugins: [new workbox.expiration.ExpirationPlugin({ maxEntries: 50 })],
    })
  );

} else {
  console.log('ðŸš¨ Workbox failed to load');
}

// í‘¸ì‹œ ì•Œë¦¼ ì²˜ë¦¬
self.addEventListener('push', (event) => {
  console.log('ðŸš€ Push ì´ë²¤íŠ¸ ë°œìƒ:', event);

  if (!event.data) {
    console.log('âš ï¸ Push event has no data.');
    return;
  }

  let pushData = null;

  try {
    // JSON íŒŒì‹± ì‹œë„
    pushData = event.data.json();
  } catch (err) {
    // JSON íŒŒì‹± ì‹¤íŒ¨ ì‹œ í…ìŠ¤íŠ¸ë¡œ ì²˜ë¦¬
    console.warn('JSON íŒŒì‹± ì‹¤íŒ¨, ì¼ë°˜ í…ìŠ¤íŠ¸ë¡œ ì²˜ë¦¬:', err);
    pushData = { title: 'ì•Œë¦¼', body: event.data.text() };
  }

  console.log('ðŸ“¢ í‘¸ì‹œ ì•Œë¦¼ ë°ì´í„°:', pushData);

  const options = {
    body: pushData.body || 'ìƒˆë¡œìš´ ì•Œë¦¼ì´ ë„ì°©í–ˆìŠµë‹ˆë‹¤!',
    icon: pushData.icon || '/icon-192x192.png',
    badge: pushData.badge || '/icon-192x192.png',
    data: pushData.data || {},
    actions: pushData.actions || [],
    vibrate: [200, 100, 200]
  };

  event.waitUntil(
    self.registration.showNotification(pushData.title || 'ì•Œë¦¼', options)
  );
});


// ì•Œë¦¼ í´ë¦­ ì²˜ë¦¬
self.addEventListener('notificationclick', (event) => {
  event.notification.close();
  
  event.waitUntil(
    clients.matchAll({ type: 'window' }).then(clientList => {
      for (const client of clientList) {
        if (client.url === '/' && 'focus' in client) {
          return client.focus();
        }
      }
      if (clients.openWindow) {
        return clients.openWindow('/');
      }
    })
  );
});

// Service Worker ì—…ë°ì´íŠ¸ ì²˜ë¦¬
self.addEventListener('message', (event) => {
  if (event.data && event.data.type === 'SKIP_WAITING') {
    self.skipWaiting();
  }
});
